        <div>
            <form  onSubmit={handleSubmit}>
                <input type="text" name="username" placeholder="username" value={props.form.username} onChange={handleChange} ></input>
                <input type="text" name="password" placeholder="password" value={props.form.password} onChange={handleChange} ></input>
                <input type="submit" value= "Log in"/>

            </form>

        </div>
    )
}

const mapStateToProps = state => {
    return {
       form:
       {username: state.username,
       password: state.password}




    }
}

export default connect(mapStateToProps, {login, currentUser})(Login)
