import {combineReducers} from 'redux'




export default function rootReducer(state = {products:[], categories: []}, action){

    switch(action.type){
        case "FETCH_PRODUCTS":
            return {products: action.payload};
        // case "FETCH_CATEGORIES":
        //     return {categories: action.payload}
            default:
                return state
    }


}
