export default class CategoryContainer extends Component {

    state ={
        categories:[
            {
                name: '',
                image: ''
            }
        ]
    }
    componentDidMount (){
        fetch("http://localhost:3001/categories")
        .then(res => res.json())
        .then(data => data.map(category => this.setState({
            categories:[
                {
                name: data.name,
                image: data.image
                }

            ]
        })) )
