import React, { Component } from 'react'

export default class CategoryContainer extends Component {

    state ={
        categories:[

        ]
    }
    componentDidMount (){
        fetch("http://localhost:3001/categories")
        .then(res => res.json())
        .then(data => this.setState({
            categories: data
        }))



    }


    render() {

        let catArray = this.state.categories.map(cat => <div>{cat.name} {cat.picture}</div>)
        return (
            <div>
                {catArray}
            </div>
        )
    }
}
