import React, { Component } from 'react'
import {BrowserRouter, Route, Link} from 'react-router-dom'
import { connect } from "react-redux"
import { findCurrentUser } from '../actions/currentUser'
import Login from './Login'



function ProductPage (props) {

     const product = props.location.state
    const addToOrder = () => {
        fetch("http://localhost:3001/orders/1/order_products", {
            method: "POST",
            headers: {
                'Content-Type': 'application/json',
                'Accept': "application/json"
            }, 
            body: JSON.stringify({
                name: product.name,
                price: product.price,
                image: product.image,
                description: product.description
            })
            .then(res => res.json())
            .then(data => console.log("success", data))
        })
    }
        return (

            <div>
                <div className="product-info">
                    <div className="product-info-img">
                    <img  src={product.image}></img>
                    </div>

                    <div className="product-info-details">
                        <ul>
                            <li>
                                <h3>{product.name}</h3>
                            </li>
                            <li>
                                <b>{product.price}</b>
                            </li>
                            <div>
                                <b>Description:</b><br>
                                </br>
                                {product.description}
                            </div>

                                <button onClick={(e, product) => addToOrder()}>Add To Order</button>
                        </ul>

                    </div>


                </div>
            </div>


        )

}
const mapStateToProps = state => {
    return{
        currentUser: state.currentUser
    }

}

export default connect(mapStateToProps, {findCurrentUser})(ProductPage);
