            <ul>
                <li>
                    <h3>{cartItem.name}</h3>
                </li>
                <li>
                    <b>$ {cartItem.price}</b>
                </li>
                <div>
                    <b>Description:</b><br>
                    </br>
                    {cartItem.description}
                    <button onClick={removeItemFromCart}>Checkout</button>
                </div>


            </ul>

        </div>


    </div>
</div> )

    return (
        <div>
            {renderedCart}

        </div>
    )
}

const mapStateToProps = state => {
    return {
        cart: state.cart
    }
}
export default connect(mapStateToProps)(Cart)
