import React, { Component } from 'react'

export default class CategoryContainer extends Component {

    state ={
        categories:[

        ]
    }
    componentDidMount (){
        fetch("http://localhost:3001/categories")
        .then(res => res.json())
        .then(data => this.setState({
            categories: data
        }))



    }
     closeMenu = () => {
     document.querySelector(".sidebar").classList.remove("open")
      }



    render() {

        let catArray = this.state.categories.map(cat => <li key={cat.id}>{cat.name} {cat.picture}</li>)
        return (
            <div>
               <aside className="sidebar">
        <h3>Equipment Categories</h3>
        <button className="sidebar-close-button" onClick={this.closeMenu}>X</button>
        <ul>
          <li>
            <a href="index.html">Barbells</a>
          </li>
          {catArray}



       </ul>
      </aside>
            </div>
        )
    }
}
