import React from 'react'
import { connect } from 'react-redux'
import { login } from '../actions/login'
import { currentUser } from '../actions/currentUser'



function Login(props) {

    const handleChangeUsername = e => {

        const { name, value } = e.target

         const formInfo ={

             name: value


         }
         props.login(formInfo)




    }

    const handleChangePassword = e => {
        const { name, value } = e.target

        const formInfo ={

            name: value


        }
        props.login(formInfo)
    }

    console.log(props)

    const handleSubmit = (e) => {
        e.preventDefault()

    }
    return (
        <div>
            <form  onSubmit={handleSubmit}>
                <input type="text" name="username" placeholder="username" value={props.username} onChange={handleChangeUsername} ></input>
                <input type="text" name="password" placeholder="password" value={props.password} onChange={handleChangePassword} ></input>
                <input type="submit" value= "Log in"/>

            </form>

        </div>
    )
}

const mapStateToProps = state => {
    return {
       username: state.username,
       password: state.password



    }
}

export default connect(mapStateToProps, {login, currentUser})(Login)
