import React from 'react'
import { useHistory } from 'react-router-dom'
import { connect } from 'react-redux'
import { setShoppingCart} from '../actions/cart'


function Cart(props) {
    const history = useHistory()

    // const cart = JSON.parse(localStorage.getItem('cart'))


    // let newArray = []

    // newArray.push(cart)

    // const usableCart = newArray[0]

    // useEffect(()=>{
    //     props.setShoppingCart(usableCart)
    // }, [])



   const renderedCart =  props.cart.map((cartItem, i) =>  <div id={i}>
    <div className="product-info">
        <div className="product-info-img">
            <img  src={cartItem.image}></img>
        </div>
        <div className="product-info-details">
            <ul>
                <li>
                    <h3>{cartItem.name}</h3>
                </li>
                <li>
                    <b>$ {cartItem.price}</b>
                </li>
                <br/>
                <div >
                    Quantity: 
                    <select className="Quantity">
                        <option >1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                    </select>
                </div>
                <br/>
                <div>
                    <b>Description:</b><br>
                    </br>
                    {cartItem.description}<br/>

                    <button name={i} id={i} onClick={removeItemFromCart}>Remove</button>
                </div>
            </ul>
        </div>
     </div>
</div> ,
console.log("quantity 1", document.querySelector('.Quantity'))
)

function removeItemFromCart(e){
    usableCart.splice(e.target.name, 1)
    let updatedCart = JSON.stringify(usableCart)
    localStorage.setItem('cart', updatedCart)
    let node = document.getElementById(e.target.name)
    node.parentElement.removeChild(node)
    e.target.id ? alert("Item removed from cart") : alert("Successfully checked out (where you would be redirected to checkout page)")
    // history.go(0)


    // iterate over and find matching index to button id
    // new array without previous item
    //use to update local storage

  }

  function checkOutCart(){
      localStorage.setItem('cart', [])
  }

    return (
        usableCart.length === 0 ? 
        <div>
            <h1>Cart Empty!</h1>
        </div>
        :
        <div>
            {renderedCart}
            <div className="checkout">
                <button  onClick={checkOutCart}>Checkout</button>
            </div>


        </div>

    )
}

const mapStateToProps = state => {
    return {
        cart: state.cart
    }
}
export default connect(mapStateToProps, { setShoppingCart })(Cart)
