export default (state = [], action) => {

    switch(action.type){
        case "SET_SHOPPING_CART":
            return   action.payload
        case "ADD_ITEM_TO_CART":
            return [...state, action.payload]
        case "REMOVE_ITEM_FROM_CART":
            return state.splice(action.payload, 1)

            default:
            return state
    }


}
