import React from 'react'
import { connect } from 'react-redux'

 function Cart(props) {

    function removeItemFromCart(){

    }



   const cart = JSON.parse(localStorage.getItem('cart'))
   let newArray = []
   newArray.push(cart)
   const usableCart = newArray[0].map(item => JSON.parse(item))
   console.log('cart', usableCart)

   let renderedCart = usableCart.map((cartItem, i)=>  <div key={i}>
    <div className="product-info">
        <div className="product-info-img">
        <img  src={cartItem.image}></img>
        </div>

        <div className="product-info-details">
            <ul>
                <li>
                    <h3>{cartItem.name}</h3>
                </li>
                <li>
                    <b>$ {cartItem.price}</b>
                </li>
                <div>
                    <b>Description:</b><br>
                    </br>
                    {cartItem.description}
                    <button onClick={removeItemFromCart}>Checkout</button>
                </div>


            </ul>

        </div>


    </div>
</div> )

    return (
        <div>
            {renderedCart}

        </div>
    )
}

const mapStateToProps = state => {
    return {
        cart: state.cart
    }
}
export default connect(mapStateToProps)(Cart)
