import {combineReducers} from 'redux'



const rootReducer = {
    products: productReducer,
    categories: categoryReducer
}


export default rootReducer

function productReducer(state = {products:[], categories: []}, action){

    switch(action.type){
        case "FETCH_PRODUCTS":
            return {products: action.payload}

            default:
                return state
    }


}
