import React, { Component } from 'react'
import { connect } from 'react-redux'
import { login }  from '../actions/currentUser'
import { findCurrentUser } from '../actions/currentUser'
import NavbarLoggedOut from './NavbarLoggedOut'



class Login extends Component  {

    state = {
        username:'',
        password:''
    }

    handleChange = e => {
        this.setState({
            [e.target.name]: e.target.value
        })

}


handleSubmit = (e) => {
        e.preventDefault()
        this.props.login(this.state)


    }

    render(){ 


        return (

        <div>
            <NavbarLoggedOut/>
            <form  onSubmit={this.handleSubmit}>
                <input type="text" name="username" placeholder="username"  onChange={this.handleChange} ></input>
                <input type="text" name="password" placeholder="password"  onChange={this.handleChange} ></input>
                <input type="submit" value= "Log in" />
        </form>

        </div>



    )}

}

const mapStateToProps = state => {
    return{
      currentUser: state.currentUser
    }
  }

export default connect(mapStateToProps, { login, findCurrentUser })(Login)
